    private void convertirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_convertirActionPerformed
        String a = binario.getText();
        actividades acti = new actividades();
        int b = acti.Bin_to_dec(a);
        String b1 = String.valueOf(b);
        decimal.setText(b1);
    }//GEN-LAST:event_convertirActionPerformed

    private void ConvertirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ConvertirActionPerformed
        String a = Dec.getText();
        String b = Rmax.getText();
        String c = Rmin.getText();
        String d = Lcadena.getText();
        actividades acti = new actividades();
        double func = acti.Dec_to_real(a, b, c, d);
        String res = String.valueOf(func);
        Real.setText(res);
    }//GEN-LAST:event_ConvertirActionPerformed

    private void changeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_changeActionPerformed
        String real = Realistic.getText();
        double re = Double.parseDouble(real);
        actividades acti = new actividades();
        double func = acti.Real_to_adaptation(re);
        String res = String.valueOf(func);
        adapta.setText(res);
    }//GEN-LAST:event_changeActionPerformed

